import { Box, Button, IconButton, Typography, useTheme, ListItemIcon } from "@mui/material";
import { tokens } from "../../theme";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { useState, useEffect } from "react";
import { ExpandMore } from "@mui/icons-material";



const Dashboard = () => {
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);

  // a state variable to keep track of the anchor element for the dropdown menu. This variable will be null initially and will be updated when the user clicks on the box
  const [anchorEl, setAnchorEl] = useState(null);
  const [anchorEl1, setAnchorEl1] = useState(null); //for misc sensors
  const [anchorEl2, setAnchorEl2] = useState(null); //for soil conditions sensors

  //two functions, one to handle the opening of the dropdown menu and another to handle the closing of the dropdown menu.
  const handleOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  //for misc sensors
  const handleOpen1 = (event) => {
    setAnchorEl1(event.currentTarget);
  };

  const handleClose1 = () => {
    setAnchorEl1(null);
  };

  //for soil conditions sensors
  const handleOpen2 = (event) => {
    setAnchorEl2(event.currentTarget);
  };

  const handleClose2 = () => {
    setAnchorEl2(null);
  };


//handles the text input for the "growing degree days" boxes
  const [startDate, setStartDate] = useState("");
//handles the text input for the "growing degree days" boxes
  const [endDate, setEndDate] = useState("");
//handles the text input for the "growing degree days" boxes
  const handleStartDateChange = (event) => {
    setStartDate(event.target.value);
  };
//handles the text input for the "growing degree days" boxes
  const handleEndDateChange = (event) => {
    setEndDate(event.target.value);
  };
//for live date and time
function getCurrentDateTimeInCST() {
  const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
  const date = new Date().toLocaleDateString('en-US', { timeZone: 'America/Chicago' });
  const dayOfWeek = daysOfWeek[new Date().getDay()];
  const time = new Date().toLocaleTimeString('en-US', { timeZone: 'America/Chicago' });
  return `${dayOfWeek} | ${date} | ${time} CST`;
}
//for the live gps coordinates of your location
  // const [latitude, setLatitude] = useState(null);
  // const [longitude, setLongitude] = useState(null);

  // useEffect(() => {
  //   navigator.geolocation.getCurrentPosition(
  //     position => {
  //       setLatitude(position.coords.latitude);
  //       setLongitude(position.coords.longitude);
  //     },
  //     error => console.error(error)
  //   );
  // }, []);

  // function formatCoordinates(latitude, longitude) {
  //   if (!latitude || !longitude) {
  //     return 'Unknown';
  //   }
  //   return `${latitude.toFixed(4)}, ${longitude.toFixed(4)}`;
  // }

  return (
    //parent style for all 3 main columns
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(3, 1fr)',
      gridGap: '10px',
      // margin: '0 auto',
      // maxWidth: '800px',
      height: '100vh',
       //the height of the parent container will be set to 100vh, which means it will take up the full height of the viewport. The width will adjust automatically based on the size of the screen. Note that this will cause the contents of each container to overflow horizontally if the screen is too narrow.
    }}
    >
      {/* style for column 1 */}
      <div style={{
        backgroundColor: 'transparent',
        height: '100%',
        display: 'flex',
        flexDirection: 'column', // this makes it so all the boxes in column 1 are stacked like a column on top of each other
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        {/* box that surrounds fielddock logo */}
        <div style={{
          backgroundColor: 'transparent',
          height: '100px',
          display: 'flex',
          // marginTop: '14rem',
          alignItems: 'start',
          justifyContent: 'center',
          flexDirection: 'column', //this makes it so all the boxes in BOX 1 are stacked like a column  on top of eachother
        }}>
          {/* box that CONTAINS fielddock logo */}
          <div style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "500px",
            height: "100px",
            backgroundColor: "transparent",
            borderRadius: "10px",
          }}>
            <object 
              data="../../assets/FieldDock-Logo.svg" 
              type="image/svg+xml" 
              style={{ maxWidth: "100%", maxHeight: "100%" }}
              width="600px"
              height="200px"
            />
          </div>
        </div>
        <div> {/* "Select FieldDock System....START" */}
          <Box 
            onClick={handleOpen}
            sx={{ border: "1px solid #00e1b4", borderRadius: "4px", padding: "10px" }}
            display="flex"
            justifyContent="space-between" // set the horizontal spacing between child elements
            alignItems="center"
            width="500px"
            height="30px"
            backgroundColor="transparent"
            borderRadius="10px"
            fontSize={"20px"}
            marginTop="2rem"
          >
            Select FieldDock System...
            <ListItemIcon>
              <ExpandMore />
            </ListItemIcon>
          </Box>
          <Menu 
            anchorEl={anchorEl} 
            open={Boolean(anchorEl)} 
            onClose={handleClose}
          >
            <MenuItem 
              onClick={handleClose}
              sx={{ color: "#000000", backgroundColor: "#BEBEBE", "&:hover": { backgroundColor: "#EBECF0" } }}
            >
              FieldDock 0001
            </MenuItem>
            <MenuItem 
              onClick={handleClose}
              sx={{ color: "#000000", backgroundColor: "#BEBEBE","&:hover": { backgroundColor: "#EBECF0" } }}
            >
              FieldDock 0002
            </MenuItem>
            <MenuItem 
              onClick={handleClose}
              sx={{ color: "#000000", backgroundColor: "#BEBEBE", "&:hover": { backgroundColor: "#EBECF0" } }}
            >
              FieldDock 0003
            </MenuItem>
          </Menu>
        </div>{/* "Select FieldDock System....END" */}
        <div>
          {/* Growing Degree Days....START */}
          <Box //main outside box
            style={{
              border: "1px solid #CCC",
              padding: "10px",
              position: "relative",
              marginTop: "2rem",
              width: "500px",
              height: "100px",
              borderRadius: "10px",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <Box
              style={{
                position: "absolute",
                top: "-15px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "#181818",
                padding: "0 5px",
                zIndex: 1,
                fontSize: "20px",
              }}
            >
              Growing Degree Days
            </Box>
            <Box //start date box
              style={{
                border: "1px solid #CCC",
                width: "25%",
                height: "50px",
                display: "inline-block",
                borderRadius: "5px",
                fontSize: "20px",
                marginRight: "10px",
              }}
            >
              <input
                type="text"
                style={{
                  width: "100%",
                  height: "100%",
                  border: "none",
                  background: "none",
                  color: "#999",
                  fontSize: "20px",
                  textAlign: "center",
                  outline: "none",
                  fontSize: "15px",
                }}
                placeholder="Start Date"
                value={startDate}
                onChange={handleStartDateChange}
              />
            </Box>
            <Box //end date box
              style={{
                border: "1px solid #CCC",
                width: "25%",
                height: "50px",
                display: "inline-block",
                borderRadius: "5px",
                fontSize: "20px",
                marginRight: "10px",
              }}
            >
              <input
                type="text"
                style={{
                  width: "100%",
                  height: "100%",
                  border: "none",
                  background: "none",
                  color: "#999",
                  fontSize: "15px",
                  textAlign: "center",
                  outline: "none",
                }}
                placeholder="End Date"
                value={endDate}
                onChange={handleEndDateChange}
              />
            </Box>
            <Box //box for Enter button
              style={{
                border: "1px solid #f59331",
                width: "25%",
                height: "50px",
                display: "inline-block",
                borderRadius: "5px",
              }}
            >
              <button
                style={{
                  width: "100%",
                  height: "100%",
                  border: "none",
                  background: "none",
                  color: "#ffffff",
                  borderRadius: "10px",
                  fontSize: "20px",
                  cursor: "pointer",
                }}
              >
                Enter
              </button>
            </Box>
            <Box //box for GDD and 000 measurement
              style={{
                width: "25%",
                height: "40px",
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: "5px",
              }}
            >
              <Box
                style={{
                  color: "#FFF",
                  fontSize: "15px",
                }}
              >
                GDD:
              </Box>
              <Box
                style={{
                  color: "#999",
                  fontSize: "15px",
                }}
              >
                000
              </Box>
            </Box>
          </Box>
        </div>
        <div>  {/*misc box starts here*/}
          <Box  
            style={{
              border: "1px solid #CCC",
              padding: "10px",
              position: "relative",
              marginTop: "2rem",
              width: "500px",
              height: "300px",
              // marginLeft: "200px",
              borderRadius: "10px"
            }}
          >
            <Box
              style={{
                position: "absolute",
                top: "-15px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "#181818",
                padding: "0 5px",
                zIndex: 1,
                fontSize: "20px"
              }}
            >
              Misc
            </Box>
            <div> {/* "Select misc sensor....START" */}
              <Box 
                onClick={handleOpen1}
                sx={{ border: "1px solid #00e1b4", borderRadius: "4px", padding: "10px" }}
                display="flex"
                justifyContent="space-between" // set the horizontal spacing between child elements
                alignItems="center"
                width="460px"
                marginTop="1rem"
                height="30px"
                backgroundColor="transparent"
                borderRadius="10px"
                fontSize={"17px"}
                style={{ marginLeft: "10px" }} 
              >
                Select a sensor...
                <ListItemIcon>
                  <ExpandMore />
                </ListItemIcon>
              </Box>
              <Menu 
                anchorEl={anchorEl1} 
                open={Boolean(anchorEl1)} 
                onClose={handleClose1}
              >
                <MenuItem 
                  onClick={handleClose1}
                  sx={{ color: "#000000", backgroundColor: "#BEBEBE", "&:hover": { backgroundColor: "#EBECF0" } }}
                >
                  FieldDock 0001
                </MenuItem>
                <MenuItem 
                  onClick={handleClose1}
                  sx={{ color: "#000000", backgroundColor: "#BEBEBE","&:hover": { backgroundColor: "#EBECF0" } }}
                >
                  FieldDock 0002
                </MenuItem>
                <MenuItem 
                  onClick={handleClose1}
                  sx={{ color: "#000000", backgroundColor: "#BEBEBE", "&:hover": { backgroundColor: "#EBECF0" } }}
                >
                  FieldDock 0003
                </MenuItem>
              </Menu>
            </div> {/* "Select misc sensor....END" */}
          </Box>
        </div>{/* Misc END */}
        <div>
          {/* Soil Conditions START */}
          <Box  //main outside box
            style={{
              border: "1px solid #CCC",
              padding: "10px",
              position: "relative",
              marginTop: "2rem",
              width: "500px",
              height: "200px",
              // marginLeft: "200px",
              borderRadius: "10px"
            }}
          >
            <Box
              style={{
                position: "absolute",
                top: "-15px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "#181818",
                padding: "0 5px",
                zIndex: 1,
                fontSize: "20px"
              }}
            >
              Soil Conditions
            </Box>
            <div> {/* "Select soil sensor....START" */}
              <Box 
                onClick={handleOpen2}
                sx={{ border: "1px solid #00e1b4", borderRadius: "4px", padding: "10px" }}
                display="flex"
                justifyContent="space-between" // set the horizontal spacing between child elements
                alignItems="center"
                width="460px"
                marginTop="1rem"
                height="30px"
                backgroundColor="transparent"
                borderRadius="10px"
                fontSize={"17px"}
                style={{ marginLeft: "10px" }} 
              >
                Select a sensor...
                <ListItemIcon>
                  <ExpandMore />
                </ListItemIcon>
              </Box>
              <Menu 
                anchorEl={anchorEl2} 
                open={Boolean(anchorEl2)} 
                onClose={handleClose2}
              >
                <MenuItem 
                  onClick={handleClose2}
                  sx={{ color: "#000000", backgroundColor: "#BEBEBE", "&:hover": { backgroundColor: "#EBECF0" } }}
                >
                  FieldDock 0001
                </MenuItem>
                <MenuItem 
                  onClick={handleClose2}
                  sx={{ color: "#000000", backgroundColor: "#BEBEBE","&:hover": { backgroundColor: "#EBECF0" } }}
                >
                  FieldDock 0002
                </MenuItem>
                <MenuItem 
                  onClick={handleClose2}
                  sx={{ color: "#000000", backgroundColor: "#BEBEBE", "&:hover": { backgroundColor: "#EBECF0" } }}
                >
                  FieldDock 0003
                </MenuItem>
              </Menu>
            </div> {/* "Select soil sensor....END" */}
          </Box>
        </div>
      </div> {/* END of BOX 1 ENTIRELY" */}


      {/*style for entire surrounding box 2, COLUMN 2*/}
      <div style={{
        backgroundColor: 'transparent',
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        {/*this is code for box surrounding User and Logout boxes*/}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          padding: '10px',
          marginBottom: '10px',
        }}>
          {/*this is code for box surrounding User*/}
          <div style={{
            marginRight: '10px', //space between user and logout button
            border: '1px solid #CCC',
            borderRadius: '5px',
            width: '200px',
            height: '28px',
            textAlign: 'center',
          }}>
            User 0000 (------)
          </div>
          {/*this is code for box surrounding Button*/}
          <button style={{
            border: '1px solid orange',
            backgroundColor: 'transparent',
            color: '#ffffff',
            padding: '5px 10px',
            borderRadius: '5px',
            cursor: "pointer",
          }}>
            Log Out
          </button>
        </div>
        {/* Last reading taken box below*/}
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          marginBottom: '1px',
        }}>
          <div>Last reading taken:</div>
          <div style={{ color: '#00e1b4' }}>
            {getCurrentDateTimeInCST()}
          </div>
        </div>
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          marginBottom: '10px',
        }}>
          <div>GPS:</div>
          <div style={{ color: '#00e1b4' }}>
            <div>38&#176;39'34.7"N 90&#176;19'58.9"W</div>
          </div>
        </div>
        {/* System Status Box starts here */}
        <div>
          <Box //box and border style for "system status box"
              style={{
                border: "1px solid #CCC",
                padding: "10px",
                position: "relative",
                width: "400px",
                height: "100px",
                borderRadius: "10px",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                flexDirection: 'column',
                marginBottom: '2rem',
                marginTop: '2rem',
              }}
            >
              <Box //box that contains the words "System Status" so that it shows up infront of the border
                style={{
                  position: "absolute",
                  top: "-15px",
                  left: "50%",
                  transform: "translateX(-50%)",
                  backgroundColor: "#181818",
                  padding: "0 5px",
                  zIndex: 1,
                  fontSize: "20px",
                }}
              >
                System Status
              </Box>
              <div style={{ //This creates a box around "drone battery" and "done status"
                display: "flex", 
                justifyContent: "space-between", 
                width: "100%",
                marginTop: "1rem",
              }}>
                <div>Drone Battery: <span style={{ color: "#00e1b4", marginLeft: "15px" }}>34.21%</span></div>  
                <div>Done Status: <span style={{ color: "#00e1b4", marginLeft: "10px" }}>Charging</span></div>
              </div>
              <div style={{ //This creates a box around "garage Battery" and "Cellular"
                display: "flex", 
                justifyContent: "space-between", 
                width: "100%",
                marginBottom: "1rem",
              }}>
