import { CSSProperties } from 'react'
import { SpringValue, Interpolation, animated } from '@react-spring/web'
import { useTheme } from '@nivo/core'
import { DatumWithArcAndColor } from '../types'

const staticStyle: CSSProperties = {
    pointerEvents: 'none',
}

export interface ArcLabelProps<Datum extends DatumWithArcAndColor> {
    datum: Datum
    label: string
    style: {
        progress: SpringValue<number>
        transform: Interpolation<string>
        textColor: string
    }
}

export const ArcLabel = <Datum extends DatumWithArcAndColor>({
    label,
    style,
}: ArcLabelProps<Datum>) => {
    const theme = useTheme()

    return (
        <animated.g transform={style.transform} opacity={style.progress} style={staticStyle}>
            <animated.text
                textAnchor="middle"
                dominantBaseline="central"
                style={{
                    ...theme.labels.text,
                    fill: style.textColor,
                }}
            >
                {label}
            </animated.text>
        </animated.g>
    )
}
import { useMemo } from 'react'
import { radiansToDegrees, positionFromAngle, degreesToRadians } from '@nivo/core'
import { DatumWithArc } from './types'

/**
 * Make sure an angle (expressed in radians)
 * always falls in the range 0~2*PI.
 */
export const getNormalizedAngle = (angle: number) => {
    let normalizedAngle = angle % (Math.PI * 2)
    if (normalizedAngle < 0) {
        normalizedAngle += Math.PI * 2
    }

    return normalizedAngle
}

/**
 * Filter out arcs with a length below `skipAngle`.
 */
export const filterDataBySkipAngle = <Datum extends DatumWithArc>(
    data: Datum[],
    skipAngle: number
) =>
    data.filter(
        datum => Math.abs(radiansToDegrees(datum.arc.endAngle - datum.arc.startAngle)) >= skipAngle
    )

/**
 * Memoized version of `filterDataBySkipAngle`.
 */
export const useFilteredDataBySkipAngle = <Datum extends DatumWithArc>(
    data: Datum[],
    skipAngle: number
) => useMemo(() => filterDataBySkipAngle(data, skipAngle), [data, skipAngle])

export const svgEllipticalArcCommand = (
    radius: number,
    largeArcFlag: 0 | 1,
    sweepFlag: 0 | 1,
    x: number,
    y: number
) =>
    [
        'A',
        radius,
        radius,
        0, // x-axis-rotation
        largeArcFlag,
        sweepFlag,
        x,
        y,
    ].join(' ')

export const generateSvgArc = (
    radius: number,
    originalStartAngle: number,
    originalEndAngle: number
): string => {
    const startAngle = Math.min(originalStartAngle, originalEndAngle)
    const endAngle = Math.max(originalStartAngle, originalEndAngle)

    const start = positionFromAngle(degreesToRadians(endAngle), radius)
    const end = positionFromAngle(degreesToRadians(startAngle), radius)

    // we have a full circle, we cannot use a single elliptical arc
    // to draw it, so we use 2 in that case.
    if (endAngle - startAngle >= 360) {
        const mid = positionFromAngle(degreesToRadians(startAngle + 180), radius)

        return [
            `M ${start.x} ${start.y}`,
            svgEllipticalArcCommand(radius, 1, 1, mid.x, mid.y),
            `M ${start.x} ${start.y}`,
            svgEllipticalArcCommand(radius, 1, 0, mid.x, mid.y),
        ].join(' ')
    }

    const largeArcFlag = endAngle - startAngle <= 180 ? 0 : 1

    return [
        `M ${start.x} ${start.y}`,
        svgEllipticalArcCommand(radius, largeArcFlag, 0, end.x, end.y),
    ].join(' ')
}
import { Arc, DatumWithArc } from './types'
import { useMemo } from 'react'

export interface ArcTransitionModeConfig {
    enter: (arc: Arc) => Arc
    update: (arc: Arc) => Arc
    leave: (arc: Arc) => Arc
}

export const arcTransitionModes = [
    'startAngle',
    'middleAngle',
    'endAngle',
    'innerRadius',
    'centerRadius',
    'outerRadius',
    'pushIn',
    'pushOut',
] as const
export type ArcTransitionMode = typeof arcTransitionModes[number]

export const arcTransitionModeById: Record<ArcTransitionMode, ArcTransitionModeConfig> = {
    startAngle: {
        enter: (arc: Arc) => ({
            ...arc,
            endAngle: arc.startAngle,
        }),
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => ({
            ...arc,
            startAngle: arc.endAngle,
        }),
    },
    middleAngle: {
        enter: (arc: Arc) => {
            const middleAngle = arc.startAngle + (arc.endAngle - arc.startAngle) / 2

            return {
                ...arc,
                startAngle: middleAngle,
                endAngle: middleAngle,
            }
        },
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => {
            const middleAngle = arc.startAngle + (arc.endAngle - arc.startAngle) / 2

            return {
                ...arc,
                startAngle: middleAngle,
                endAngle: middleAngle,
            }
        },
    },
    endAngle: {
        enter: (arc: Arc) => ({
            ...arc,
            startAngle: arc.endAngle,
        }),
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => ({
            ...arc,
            endAngle: arc.startAngle,
        }),
    },
    innerRadius: {
        enter: (arc: Arc) => ({
            ...arc,
            outerRadius: arc.innerRadius,
        }),
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => ({
            ...arc,
            innerRadius: arc.outerRadius,
        }),
    },
    centerRadius: {
        enter: (arc: Arc) => {
            const centerRadius = arc.innerRadius + (arc.outerRadius - arc.innerRadius) / 2

            return {
                ...arc,
                innerRadius: centerRadius,
                outerRadius: centerRadius,
            }
        },
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => {
            const centerRadius = arc.innerRadius + (arc.outerRadius - arc.innerRadius) / 2

            return {
                ...arc,
                innerRadius: centerRadius,
                outerRadius: centerRadius,
            }
        },
    },
    outerRadius: {
        enter: (arc: Arc) => ({
            ...arc,
            innerRadius: arc.outerRadius,
        }),
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => ({
            ...arc,
            outerRadius: arc.innerRadius,
        }),
    },
    pushIn: {
        enter: (arc: Arc) => ({
            ...arc,
            innerRadius: arc.innerRadius - arc.outerRadius + arc.innerRadius,
            outerRadius: arc.innerRadius,
        }),
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => ({
            ...arc,
            innerRadius: arc.outerRadius,
            outerRadius: arc.outerRadius + arc.outerRadius - arc.innerRadius,
        }),
    },
    pushOut: {
        enter: (arc: Arc) => ({
            ...arc,
            innerRadius: arc.outerRadius,
            outerRadius: arc.outerRadius + arc.outerRadius - arc.innerRadius,
        }),
        update: (arc: Arc) => arc,
        leave: (arc: Arc) => ({
            ...arc,
            innerRadius: arc.innerRadius - arc.outerRadius + arc.innerRadius,
            outerRadius: arc.innerRadius,
        }),
    },
}

export interface TransitionExtra<Datum extends DatumWithArc, ExtraProps> {
    enter: (datum: Datum) => ExtraProps
    update: (datum: Datum) => ExtraProps
    leave: (datum: Datum) => ExtraProps
}

export const useArcTransitionMode = <Datum extends DatumWithArc, ExtraProps>(
    mode: ArcTransitionMode,
    extraTransition?: TransitionExtra<Datum, ExtraProps>
) =>
    useMemo(() => {
        const transitionMode = arcTransitionModeById[mode]

        return {
            enter: (datum: Datum) => ({
                progress: 0,
                ...transitionMode.enter(datum.arc),
                ...(extraTransition ? extraTransition.enter(datum) : {}),
            }),
            update: (datum: Datum) => ({
                progress: 1,
                ...transitionMode.update(datum.arc),
                ...(extraTransition ? extraTransition.update(datum) : {}),
            }),
            leave: (datum: Datum) => ({
                progress: 0,
                ...transitionMode.leave(datum.arc),
                ...(extraTransition ? extraTransition.leave(datum) : {}),
            }),
        }
    }, [mode, extraTransition])
import { useMemo } from 'react'
import { useTransition, to, SpringValue } from '@react-spring/web'
import {
    // @ts-ignore
    midAngle,
    positionFromAngle,
    useMotionConfig,
} from '@nivo/core'
import { Arc, DatumWithArc, Point } from './types'
import { filterDataBySkipAngle } from './utils'
import { ArcTransitionMode, TransitionExtra, useArcTransitionMode } from './arcTransitionMode'

export const computeArcCenter = (arc: Arc, offset: number): Point => {
    const angle = midAngle(arc) - Math.PI / 2
    const radius = arc.innerRadius + (arc.outerRadius - arc.innerRadius) * offset

    return positionFromAngle(angle, radius)
}

export const interpolateArcCenter =
    (offset: number) =>
    (
        startAngleValue: SpringValue<number>,
        endAngleValue: SpringValue<number>,
        innerRadiusValue: SpringValue<number>,
        outerRadiusValue: SpringValue<number>
    ) =>
        to(
            [startAngleValue, endAngleValue, innerRadiusValue, outerRadiusValue],
            (startAngle, endAngle, innerRadius, outerRadius) => {
                const centroid = computeArcCenter(
                    { startAngle, endAngle, innerRadius, outerRadius },
                    offset
                )

                return `translate(${centroid.x},${centroid.y})`
            }
        )

export const useArcCentersTransition = <Datum extends DatumWithArc, ExtraProps = unknown>(
    data: Datum[],
    // define where the centers should be placed,
    // 0.0: inner radius
    // 0.5: center
    // 1.0: outer radius
    offset = 0.5,
    mode: ArcTransitionMode = 'innerRadius',
    extra?: TransitionExtra<Datum, ExtraProps>
) => {
    const { animate, config: springConfig } = useMotionConfig()

    const phases = useArcTransitionMode<Datum, ExtraProps>(mode, extra)

    const transition = useTransition<
        Datum,
        {
            progress: number
            startAngle: number
            endAngle: number
            innerRadius: number
            outerRadius: number
        } & ExtraProps
    >(data, {
        keys: datum => datum.id,
        initial: phases.update,
        from: phases.enter,
        enter: phases.update,
        update: phases.update,
        leave: phases.leave,
        config: springConfig,
        immediate: !animate,
    })

    return {
        transition,
        interpolate: interpolateArcCenter(offset),
    }
}

export interface ArcCenter<Datum extends DatumWithArc> extends Point {
    data: Datum
}

/**
 * Compute an array of arc centers from an array of data containing arcs.
 *
 * If you plan to animate those, you could use `useArcCentersTransition`
 * instead, you could use the returned array with react-spring `useTransition`,
 * but this would lead to cartesian transitions (x/y), while `useArcCentersTransition`
 * will generate proper transitions using radius/angle.
 */
export const useArcCenters = <
    Datum extends DatumWithArc,
    ExtraProps extends Record<string, any> = Record<string, any>
>({
    data,
    offset = 0.5,
    skipAngle = 0,
    computeExtraProps = () => ({} as ExtraProps),
}: {
    data: Datum[]
    // define where the centers should be placed,
    // 0.0: inner radius
    // 0.5: center
    // 1.0: outer radius
    offset?: number
    // arcs with a length below this (end angle - start angle in degrees)
    // are going to be excluded, this can typically be used to avoid having
    // overlapping labels.
    skipAngle?: number
    // this can be used to append extra properties to the centers,
    // can be used to compute a color/label for example.
    computeExtraProps?: (datum: Datum) => ExtraProps
}): (ArcCenter<Datum> & ExtraProps)[] =>
    useMemo(
        () =>
            filterDataBySkipAngle<Datum>(data, skipAngle)
                // compute position and extra props for each eligible datum
                .map(datum => {
                    const position = computeArcCenter(datum.arc, offset)

                    return {
                        ...computeExtraProps(datum),
                        x: position.x,
                        y: position.y,
                        data: datum,
                    }
                }),
        [data, offset, skipAngle, computeExtraProps]
    )
import { createElement, useMemo } from 'react'
import { PropertyAccessor, usePropertyAccessor, radiansToDegrees, useTheme } from '@nivo/core'
import { useInheritedColor } from '@nivo/colors'
import { useArcCentersTransition } from '../centers'
import { ArcTransitionMode } from '../arcTransitionMode'
import { DatumWithArcAndColor } from '../types'
import { ArcLabelsProps } from './props'
import { ArcLabel, ArcLabelProps } from './ArcLabel'

export type ArcLabelComponent<Datum extends DatumWithArcAndColor> = (
    props: ArcLabelProps<Datum>
) => JSX.Element

interface ArcLabelsLayerProps<Datum extends DatumWithArcAndColor> {
    center: [number, number]
    data: Datum[]
    label: PropertyAccessor<Datum, string>
    radiusOffset: ArcLabelsProps<Datum>['arcLabelsRadiusOffset']
    skipAngle: ArcLabelsProps<Datum>['arcLabelsSkipAngle']
    textColor: ArcLabelsProps<Datum>['arcLabelsTextColor']
    transitionMode: ArcTransitionMode
    component?: ArcLabelsProps<Datum>['arcLabelsComponent']
}

export const ArcLabelsLayer = <Datum extends DatumWithArcAndColor>({
    center,
    data,
    transitionMode,
    label: labelAccessor,
    radiusOffset,
    skipAngle,
    textColor,
    component = ArcLabel,
}: ArcLabelsLayerProps<Datum>) => {
    const getLabel = usePropertyAccessor<Datum, string>(labelAccessor)
    const theme = useTheme()
    const getTextColor = useInheritedColor<Datum>(textColor, theme)

    const filteredData = useMemo(
        () =>
            data.filter(datum => {
                return (
                    Math.abs(radiansToDegrees(datum.arc.endAngle - datum.arc.startAngle)) >=
                    skipAngle
                )
            }),
        [data, skipAngle]
    )

    const { transition, interpolate } = useArcCentersTransition<Datum>(
        filteredData,
        radiusOffset,
        transitionMode
    )

    const Label: ArcLabelComponent<Datum> = component

    return (
        <g transform={`translate(${center[0]},${center[1]})`}>
            {transition((transitionProps, datum) => {
                return createElement(Label, {
                    key: datum.id,
                    datum,
                    label: getLabel(datum),
                    style: {
                        ...transitionProps,
                        transform: interpolate(
                            transitionProps.startAngle,
                            transitionProps.endAngle,
                            transitionProps.innerRadius,
                            transitionProps.outerRadius
                        ),
                        textColor: getTextColor(datum),
                    },
                })
            })}
        </g>
    )
}
import { CompleteTheme } from '@nivo/core'
import { DatumWithArcAndColor } from '../types'
import { ArcLabel } from './useArcLabels'

export const drawCanvasArcLabels = <Datum extends DatumWithArcAndColor>(
    ctx: CanvasRenderingContext2D,
    labels: ArcLabel<Datum>[],
    theme: CompleteTheme
) => {
    ctx.textAlign = 'center'
    ctx.textBaseline = 'middle'
    ctx.font = `${theme.labels.text.fontSize}px ${theme.labels.text.fontFamily}`

    labels.forEach(label => {
        ctx.fillStyle = label.textColor
        ctx.fillText(`${label.label}`, label.x, label.y)
    })
}
import { useCallback } from 'react'
import { PropertyAccessor, usePropertyAccessor, useTheme } from '@nivo/core'
import { InheritedColorConfig, useInheritedColor } from '@nivo/colors'
import { DatumWithArcAndColor } from '../types'
import { useArcCenters, ArcCenter } from '../centers'

export interface ArcLabel<Datum extends DatumWithArcAndColor> extends ArcCenter<Datum> {
    label: string
    textColor: string
}

/**
 * Compute arc labels, please note that the datum should
 * contain a color in order to be able to compute the label text color.
 *
 * Please see `useArcCenters` for a more detailed explanation
 * about the parameters.
 */
export const useArcLabels = <Datum extends DatumWithArcAndColor>({
    data,
    offset,
    skipAngle,
    label,
    textColor,
}: {
    data: Datum[]
    offset?: number
    skipAngle?: number
    label: PropertyAccessor<Datum, string>
    textColor: InheritedColorConfig<Datum>
}) => {
    const getLabel = usePropertyAccessor<Datum, string>(label)

    const theme = useTheme()
    const getTextColor = useInheritedColor<Datum>(textColor, theme)

    const computeExtraProps = useCallback(
        (datum: Datum) => {
            return {
                label: getLabel(datum),
                textColor: getTextColor(datum),
            }
        },
        [getLabel, getTextColor]
    )

    return useArcCenters<Datum, Omit<ArcLabel<Datum>, keyof ArcCenter<Datum>>>({
        data,
        offset,
        skipAngle,
        computeExtraProps,
    })
}
import { ArcLabel } from './ArcLabel'

export const ArcLabelComponent = ArcLabel
export * from './ArcLabelsLayer'
export * from './canvas'
export * from './props'
export * from './useArcLabels'
import { useTheme } from '@nivo/core'
import { SpringValue, Interpolation, animated } from '@react-spring/web'
import { DatumWithArcAndColor } from '../types'

export interface ArcLinkLabelProps<Datum extends DatumWithArcAndColor> {
    datum: Datum
    label: string
    style: {
        path: Interpolation<string>
        thickness: number
        textPosition: Interpolation<string>
        textAnchor: Interpolation<'start' | 'end'>
        linkColor: SpringValue<string>
        opacity: SpringValue<number>
        textColor: SpringValue<string>
    }
}

export const ArcLinkLabel = <Datum extends DatumWithArcAndColor>({
    label,
    style,
}: ArcLinkLabelProps<Datum>) => {
    const theme = useTheme()

    return (
        <animated.g opacity={style.opacity}>
            <animated.path
                fill="none"
                stroke={style.linkColor}
                strokeWidth={style.thickness}
                d={style.path}
            />
            <animated.text
                transform={style.textPosition}
                textAnchor={style.textAnchor}
                dominantBaseline="central"
                style={{
                    ...theme.labels.text,
                    fill: style.textColor,
                }}
            >
                {label}
            </animated.text>
        </animated.g>
    )
}
import { positionFromAngle } from '@nivo/core'
import { Arc, Point } from '../types'
import { getNormalizedAngle } from '../utils'
import { ArcLink } from './types'

/**
 * Compute text anchor for a given arc.
 *
 * `computeArcLink` already computes a `side`, but when using
 * `react-spring`, you cannot have a single interpolation
 * returning several output values, so we need to compute
 * them in separate interpolations.
 */
export const computeArcLinkTextAnchor = (arc: Arc): 'start' | 'end' => {
    const centerAngle = getNormalizedAngle(
        arc.startAngle + (arc.endAngle - arc.startAngle) / 2 - Math.PI / 2
    )

    if (centerAngle < Math.PI / 2 || centerAngle > Math.PI * 1.5) {
        return 'start'
    }

    return 'end'
}

/**
 * Compute the link of a single arc, returning its points,
 * please note that points coordinates are relative to
 * the center of the arc.
 */
export const computeArcLink = (
    arc: Arc,
    offset: number,
    diagonalLength: number,
    straightLength: number
): ArcLink => {
    const centerAngle = getNormalizedAngle(
        arc.startAngle + (arc.endAngle - arc.startAngle) / 2 - Math.PI / 2
    )
    const point0: Point = positionFromAngle(centerAngle, arc.outerRadius + offset)
    const point1: Point = positionFromAngle(centerAngle, arc.outerRadius + offset + diagonalLength)

    let side: ArcLink['side']
    let point2: Point
    if (centerAngle < Math.PI / 2 || centerAngle > Math.PI * 1.5) {
        side = 'after'
        point2 = {
            x: point1.x + straightLength,
            y: point1.y,
        }
    } else {
        side = 'before'
        point2 = {
            x: point1.x - straightLength,
            y: point1.y,
        }
    }

    return {
        side,
        points: [point0, point1, point2],
    }
}
import { useMemo } from 'react'
import { SpringValue, useTransition, to } from '@react-spring/web'
import { line } from 'd3-shape'
import { useMotionConfig, useTheme } from '@nivo/core'
import { InheritedColorConfig, useInheritedColor } from '@nivo/colors'
import { DatumWithArcAndColor, Point } from '../types'
import { useFilteredDataBySkipAngle } from '../utils'
import { computeArcLink, computeArcLinkTextAnchor } from './compute'

const lineGenerator = line<Point>()
    .x(d => d.x)
    .y(d => d.y)

type AnimatedProps = {
    startAngle: number
    endAngle: number
    innerRadius: number
    outerRadius: number
    offset: number
    diagonalLength: number
    straightLength: number
    textOffset: number
    linkColor: string
    textColor: string
    opacity: number
}

const useTransitionPhases = <Datum extends DatumWithArcAndColor>({
    offset,
    diagonalLength,
    straightLength,
    textOffset,
    getLinkColor,
    getTextColor,
}: Pick<AnimatedProps, 'offset' | 'diagonalLength' | 'straightLength' | 'textOffset'> & {
    getLinkColor: (datum: Datum) => string
    getTextColor: (datum: Datum) => string
}): Record<'enter' | 'update' | 'leave', (datum: Datum) => AnimatedProps> =>
    useMemo(
        () => ({
            enter: (datum: Datum) => ({
                startAngle: datum.arc.startAngle,
                endAngle: datum.arc.endAngle,
                innerRadius: datum.arc.innerRadius,
                outerRadius: datum.arc.outerRadius,
                offset,
                diagonalLength: 0,
                straightLength: 0,
                textOffset,
                linkColor: getLinkColor(datum),
                textColor: getTextColor(datum),
                opacity: 0,
            }),
            update: (d: Datum) => ({
                startAngle: d.arc.startAngle,
                endAngle: d.arc.endAngle,
                innerRadius: d.arc.innerRadius,
                outerRadius: d.arc.outerRadius,
                offset,
                diagonalLength,
                straightLength,
                textOffset,
                linkColor: getLinkColor(d),
                textColor: getTextColor(d),
                opacity: 1,
            }),
            leave: (d: Datum) => ({
                startAngle: d.arc.startAngle,
                endAngle: d.arc.endAngle,
                innerRadius: d.arc.innerRadius,
                outerRadius: d.arc.outerRadius,
                offset,
                diagonalLength: 0,
                straightLength: 0,
                textOffset,
                linkColor: getLinkColor(d),
                textColor: getTextColor(d),
                opacity: 0,
            }),
        }),
        [diagonalLength, straightLength, textOffset, getLinkColor, getTextColor]
    )

const interpolateLink = (
    startAngleValue: SpringValue<AnimatedProps['startAngle']>,
    endAngleValue: SpringValue<AnimatedProps['endAngle']>,
    innerRadiusValue: SpringValue<AnimatedProps['innerRadius']>,
    outerRadiusValue: SpringValue<AnimatedProps['outerRadius']>,
    offsetValue: SpringValue<AnimatedProps['offset']>,
    diagonalLengthValue: SpringValue<AnimatedProps['diagonalLength']>,
    straightLengthValue: SpringValue<AnimatedProps['straightLength']>
) =>
    to(
        [
            startAngleValue,
            endAngleValue,
            innerRadiusValue,
            outerRadiusValue,
            offsetValue,
            diagonalLengthValue,
            straightLengthValue,
        ],
        (
            startAngle,
            endAngle,
            innerRadius,
            outerRadius,
            offset,
            diagonalLengthAnimated,
            straightLengthAnimated
        ) => {
            const { points } = computeArcLink(
                {
                    startAngle,
                    endAngle,
                    innerRadius,
                    outerRadius,
                },
                offset,
                diagonalLengthAnimated,
                straightLengthAnimated
            )

            return lineGenerator(points)
        }
    )

const interpolateTextAnchor = (
    startAngleValue: SpringValue<AnimatedProps['startAngle']>,
    endAngleValue: SpringValue<AnimatedProps['endAngle']>,
    innerRadiusValue: SpringValue<AnimatedProps['innerRadius']>,
    outerRadiusValue: SpringValue<AnimatedProps['outerRadius']>
) =>
    to(
        [startAngleValue, endAngleValue, innerRadiusValue, outerRadiusValue],
        (startAngle, endAngle, innerRadius, outerRadius) => {
            return computeArcLinkTextAnchor({
                startAngle,
                endAngle,
                innerRadius,
                outerRadius,
            })
