import React from "react";
import { Link } from "react-router-dom";
import { Box, ListItemIcon, Typography } from "@mui/material";
import { useState, useEffect } from "react";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { ExpandMore } from "@mui/icons-material";

const [hangerAnchorEl, setHangerAnchorEl] = useState(null);
const [selectedHangerSystem, setSelectedHangerSystem] = useState(null);
const [hangerSystems, setHangerSystems] = useState([]);
const handleHangerOpen = (event) => {
  setHangerAnchorEl(event.currentTarget);
};

const handleHangerClose = () => {
  setHangerAnchorEl(null);
};

const handleHangerSelect = (event, selected) => {
  setSelectedHangerSystem(selected);
  handleHangerClose();
};

function Drone() {
  return (
    <div //parent div style of ALL ROWS
      style={{
        display: "grid",
        gridTemplateRows: "repeat(3, minmax(100px, auto))", // use grid-auto-rows instead of gridTemplateRows
        gridGap: "10px",
        margin: "0 auto",
        maxWidth: "90%",
      }}
    >
      <div // Parent div for row 1 start here
        style={{
          backgroundColor: "#ccc",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gridRow: "1", // Set the row number to 1
        }}
      >
        {/* box that surrounds fielddock logo */}
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "85%",
            height: "100px",
            backgroundColor: "transparent",
          }}
        >
          <object
            data="../../assets/FieldDock-Logo.svg"
            type="image/svg+xml"
            style={{ maxWidth: "100%", maxHeight: "100%" }}
          />
        </div>
        <div style={{ width: "85%" }}>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              height: "40px",
              backgroundColor: "transparent",
              border: "1px solid #00e1b4",
              padding: "10px",
              justifyContent: "space-between",
              marginTop: "3rem",
              borderRadius: "5px",
              fontSize: "1vw",
            }}
            onClick={handleHangerOpen}
          >
            {selectedHangerSystem
              ? selectedHangerSystem
              : "Select Hanger System..."}
            <ListItemIcon>
              <ExpandMore />
            </ListItemIcon>
          </div>

          <Menu
            anchorEl={hangerAnchorEl}
            open={Boolean(hangerAnchorEl)}
            onClose={handleHangerClose}
          >
            {hangerSystems.map((system) => (
              <MenuItem
                key={system.id}
                autoFocus={false}
                onClick={(event) =>
                  handleHangerSelect(
                    event,
                    `${system.name} - ${system.location}`
                  )
                }
                sx={{
                  color: "#000000",
                  backgroundColor: "#BEBEBE !important",
                  "&:hover": { backgroundColor: "#EBECF0 !important" },
                }}
              >
                {system.name} - {system.location}
              </MenuItem>
            ))}
          </Menu>
        </div>{" "}
        {/* end of SELECT FIELDDOCK */}
      </div>
      {/* parent div END for row 1 above */}

      <div //parent div style for row 2
        style={{
          backgroundColor: "#ccc",
          height: "200px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        Row 2
      </div>
      {/* parent style for row 2 ends above */}

      <div //parent style for row 3
        style={{
          backgroundColor: "#ccc",
          height: "200px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        Row 3
      </div>
      {/* parent ends for row 3 above */}
    </div>
    // END OF PARENT DIV OF ALL ROWS ABOVE
  );
}

export default Drone;
