import React, { useState, useMemo, useRef, useEffect } from "react";
import { useTable } from "react-table";

const LatLongTable = () => {
  const columns = useMemo(
    () => [
      // ...
      {
        Header: "Command",
        accessor: "command",
        Cell: ({ row, updateMyData }) => (
          <div style={{ width: "200px" }}>
            <select
              id={`row-${row.values.id}`}
              ref={rowRefs[row.index]}
              value={row.values.command}
              onChange={(e) =>
                updateMyData(row.index, "command", e.target.value)
              }
              style={{ width: "100%" }}
            >
              <option>WAYPOINT</option>
              <option>No</option>
              <option>Maybe</option>
              <option>So</option>
            </select>
          </div>
        ),
      },
      // ...
      {
        Header: "AZ",
        accessor: "AZ",
        Cell: ({ row, updateMyData }) => (
          <div style={{ width: "100%" }}>
            <input
              value={row.values.AZ}
              onChange={(e) => updateMyData(row.index, "AZ", e.target.value)}
              onKeyDown={(e) => keyDownHandler(e, row)}
              style={{ width: "100%" }}
            />
          </div>
        ),
      },
    ],
    []
  );

  const [data, setData] = useState([
    // ...
  ]);

  const rowRefs = data.map(() => useRef(null));

  const updateMyData = (rowIndex, columnId, value) => {
    // ...
  };

  const keyDownHandler = (e, row) => {
    if (e.key === "Tab" && row.index === data.length - 1) {
      e.preventDefault();
      setData((oldData) => [
        ...oldData,
        {
          id: oldData.length + 1,
          command: "",
          p1: "",
          p2: "",
          p3: "",
          p4: "",
          latitude: "",
          longitude: "",
          alt: "",
          frame: "",
          grad: "",
          angle: "",
          dist: "",
          az: "",
        },
      ]);
      setTimeout(() => rowRefs[data.length].current.focus(), 0);
    }
  };

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({ columns, data, updateMyData });

  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()} className="table-header">
                {column.render("Header")}
              </th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row) => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map((cell) => (
                <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
              ))}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

export default LatLongTable;
