import React, { useEffect } from "react";
import { Box } from "@mui/material";
import { useMqtt } from "react-mqtt";

const Status = () => {
  const { mqttPublish, mqttSubscribe, messages } = useMqtt({
    host: "3.145.131.67",
    port: 9001,
    protocol: "ws", // Use WebSocket protocol for MQTT
  });

  useEffect(() => {
    mqttSubscribe("drone/status"); // Subscribe to the "drone/status" topic

    // Optionally, you can publish messages to MQTT if needed
    // mqttPublish("your/topic", "your message");

    // Clean up the subscription when the component unmounts
    return () => {
      mqttSubscribe("drone/status", false); // Unsubscribe
    };
  }, [mqttSubscribe]);

  return (
    <>
      <Box
        style={{
          borderTop: "1px solid #f59331",
          width: "80%",
          height: "500px",
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-start",
          marginBottom: "15px",
        }}
      >
        <Box
          style={{
            position: "absolute",
            transform: "translate(-1%, -60%)",
            backgroundColor: "#181818",
            padding: "0 7px",
            zIndex: 1,
            fontSize: "25px",
          }}
        >
          System Controller
        </Box>
        <div
          style={{
            marginTop: "40px", // Adjust as needed to position the text
            color: "white", // Change the text color
          }}
        >
          {messages.map((message, index) => (
            <p key={index}>{message.payload}</p>
          ))}
        </div>
      </Box>
    </>
  );
};

export default Status;
