import React, { useState } from "react";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";
import axios from "axios";

const containerStyle = {
  position: "relative",
  width: "100%",
  height: "815px",
};

const initialCenter = {
  lat: 38.6742,
  lng: -122.4194,
};

const searchBoxStyles = {
  position: "absolute",
  top: "10px",
  left: "50%",
  transform: "translateX(-50%)",
  background: "white",
  border: "1px solid black",
  padding: 10,
};

const MapComponent = ({ onMapClick, markerPositions }) => {
  const [address, setAddress] = useState("");
  const [center, setCenter] = useState(initialCenter); // Add this line

  const geocode = async (address) => {
    const response = await axios.get(
      "https://maps.googleapis.com/maps/api/geocode/json",
      {
        params: {
          address: address,
          key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
        },
      }
    );

    return response.data.results[0].geometry.location;
  };

  const handleLocationSubmit = async (event) => {
    event.preventDefault();

    const coordinates = await geocode(address);

    setCenter(coordinates); // Add this line

    onMapClick({
      latLng: {
        lat: () => coordinates.lat,
        lng: () => coordinates.lng,
      },
    });
  };

  return (
    <div style={containerStyle}>
      <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center} // Update this line
          zoom={10}
          onClick={onMapClick}
        >
          {markerPositions.map((position, index) => (
            <Marker key={index} position={position} />
          ))}
        </GoogleMap>
      </LoadScript>
      <form onSubmit={handleLocationSubmit} style={searchBoxStyles}>
        <input
          type="text"
          value={address}
          onChange={(event) => setAddress(event.target.value)}
          placeholder="Enter a location"
        />
        <button type="submit" style={{ display: "none" }}>
          Go
        </button>
      </form>
    </div>
  );
};

export default MapComponent;
