import { Box, IconButton, useTheme } from "@mui/material";
import { useContext } from "react";
import { ColorModeContext, tokens } from "../../theme";
import InputBase from "@mui/material/InputBase";
import LightModeOutlinedIcon from "@mui/icons-material/LightModeOutlined"; 
import DarkModeOutlinedIcon from "@mui/icons-material/DarkModeOutlined"; 
import NotificationsOutlinedIcon from "@mui/icons-material/NotificationsOutlined"; 
import SettingsOutlinedIcon from "@mui/icons-material/SettingsOutlined"; 
import PersonOutlinedIcon from "@mui/icons-material/PersonOutlined"; 
import SearchIcon from "@mui/icons-material/Search"; 





const Topbar = () => { //this code below basically is just allowing your topbar to follow the light/dark mode scheme when toggled
    const theme = useTheme();
    const colors = tokens(theme.palette.mode);
    const colorMode = useContext(ColorModeContext);

    return (
      <Box display="flex" justifyContent="space-between" p={2}> {/*"Box" is just like a div in REact except you can write CSS straight on the box (div).  the "p" in this example is PADDING*/}
        {/* SEARCH BAR */}
        <Box
          display="flex"
          backgroundColor={colors.primary[400]}
          borderRadius="3px"
        >
          <InputBase sx={{ ml: 2, flex: 1 }} placeholder="Search" /> {/* "ml" is MARGIN-LEFT */}
          <IconButton type="button" sx={{ p: 1 }}> {/* NO FUNCTIONALITY AT THE MOMENT */}
            <SearchIcon />
          </IconButton>
        </Box>

        {/* ICONS */}
        <Box display="flex">
          <IconButton onClick={colorMode.toggleColorMode}> {/*Conditional statement allow the toggle of light and dark mode to be activated when the button is clicked with the onClick*/}
            {theme.palette.mode === "dark" ? (
              <DarkModeOutlinedIcon />
            ) : (
              <LightModeOutlinedIcon />
            )}
          </IconButton>
          <IconButton>
            <NotificationsOutlinedIcon />
          </IconButton>
          <IconButton>
            <SettingsOutlinedIcon />
          </IconButton>
          <IconButton>
            <PersonOutlinedIcon />
          </IconButton>
        </Box>
      </Box>
    );
  };

  export default Topbar
