import React from "react";
import { Link } from "react-router-dom";
import { Box, ListItemIcon, Typography } from "@mui/material";
import { useState, useEffect } from "react";

function Drone() {
  const waveAnimation = `
    @keyframes wave {
      0% {
        color: green;
        text-shadow: none;
      }
      25% {
        color: #88ff88;
        text-shadow: 0 0 5px #88ff88;
      }
      50% {
        color: green;
        text-shadow: none;
      }
      75% {
        color: #88ff88;
        text-shadow: 0 0 5px #88ff88;
      }
      100% {
        color: green;
        text-shadow: none;
      }
    }
  `;

  return (
    <div //parent div style of ALL ROWS
      style={{
        display: "grid",
        gridTemplateRows: "repeat(3, minmax(100px, auto))",
        gridGap: "10px",
        margin: "0 auto",
        maxWidth: "90%",
      }}
    >
      <div // Parent div for row 1 start here
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)", // add this property to create 3 evenly spaced boxes
          gridGap: "10px",
          backgroundColor: "#ccc",
          height: "200px",
          justifyContent: "space-between", // add this property to space the boxes evenly
        }}
      >
        <div
          style={{
            color: "green",
            fontSize: "25px",
            animation: `
        wave 2s ease-in-out infinite
      `,
            animationName: "wave", // For older browsers that don't support the animation shorthand
            animationDuration: "2s",
            animationTimingFunction: "ease-in-out",
            animationIterationCount: "infinite",
            animationFillMode: "both", // Keeps the end state of the animation
            animationPlayState: "running", // Allows pausing or resuming the animation
            animationDelay: "0s",
            textShadow: "none",
            // Apply the waveAnimation keyframes to the style
            animationKeyframes: waveAnimation,
          }}
        >
          charging
        </div>

        <div style={{ backgroundColor: "black" }}>Box 2</div>
        <div style={{ backgroundColor: "black" }}>Box 3</div>
      </div>
      {/* parent div END for row 1 above */}

      {/* ////////////////////////////row 2 start/////////////////////////////////////////////// */}

      <div //parent div style for row 2
        style={{
          backgroundColor: "#ccc",
          height: "200px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <div style={{ backgroundColor: "black" }}>Box 1</div>
      </div>
      {/* parent style for row 2 ends above */}

      {/* /////////////////////////////row 3 start////////////////////////////////////////////// */}

      <div //parent style for row 3
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)", // add this property to create 3 evenly spaced boxes
          gridGap: "10px",
          backgroundColor: "#ccc",
          height: "200px",
        }}
      >
        <div style={{ backgroundColor: "white" }}>Box 1</div>
        <div style={{ backgroundColor: "white" }}>Box 2</div>
        <div style={{ backgroundColor: "white" }}>Box 3</div>
      </div>
      {/* parent ends for row 3 above */}

      {/* ////////////////////////////////end of page//////////////////////// */}
    </div>
    // END OF PARENT DIV OF ALL ROWS ABOVE
  );
}

export default Drone;
