import React from 'react';
import { Box } from "@mui/material";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { useState, useEffect } from "react";
import { ExpandMore } from "@mui/icons-material";
import { Link } from 'react-router-dom';
// import axios from 'axios';


//for live date and time
function getCurrentDateTimeInCST() {
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const date = new Date().toLocaleDateString('en-US', { timeZone: 'America/Chicago' });
    const dayOfWeek = daysOfWeek[new Date().getDay()];
    const time = new Date().toLocaleTimeString('en-US', { timeZone: 'America/Chicago' });
    return `${dayOfWeek} | ${date} | ${time} CST`;
  }

  const handleOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleSelect = (event, sensor) => {
    setSelectedSensor(sensor);
    handleClose();
  };

const Sensors = () => {
    return (
        <div
            style={{
                backgroundColor: "transparent",
                height: "100%",
                display: "flex",
                flexDirection: "column", // this makes it so all the boxes in column 1 are stacked like a column on top of each other
                alignItems: "center",
                justifyContent: "center",
            }}
            >
            {/* Box that surrounds fielddock logo */}
            <div
                style={{
                backgroundColor: "transparent",
                height: "100px",
                display: "flex",
                alignItems: "start",
                justifyContent: "center",
                flexDirection: "column", // this makes it so all the boxes in BOX 1 are stacked like a column on top of each other
                }}
            >
                {/* Box that CONTAINS fielddock logo */}
                <div
                style={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    width: "100%", // Set width to 100% of the parent
                    height: "100px",
                    backgroundColor: "transparent",
                    borderRadius: "10px",
                }}
                >
                <object
                    data="../../assets/FieldDock-Logo.svg"
                    type="image/svg+xml"
                    style={{ maxWidth: "100%", maxHeight: "100%" }} // Apply maxWidth and maxHeight directly to the object
                    // Remove fixed width and height values
                />
                </div>
            </div>

            {/* New div box */}
            <div>
                {/* "Select Fieldodck System....START" */}
                <Box
                sx={{
                    border: "1px solid #00e1b4",
                    padding: "10px",
                    display: "flex",
                    justifyContent: "space-between", // set the horizontal spacing between child elements
                    alignItems: "center",
                    width: "500px",
                    marginTop: "1rem",
                    height: "30px",
                    backgroundColor: "transparent",
                    borderRadius: "5px",
                    fontSize: "17px",
                    marginLeft: "10px",
                }}
                onClick={handleOpen}
                >
                {selectedSensor ? selectedSensor : "Select Fielddock System..."}
                <ListItemIcon>
                    <ExpandMore />
                </ListItemIcon>
                </Box>
                <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
                <MenuItem
                    autoFocus={false}
                    onClick={(event) => handleSelect(event, "FieldDock 0001")}
                    sx={{
                    color: "#000000",
                    backgroundColor: "#BEBEBE !important",
                    "&:hover": { backgroundColor: "#EBECF0 !important" },
                    }}
                >
                    FieldDock 0001
                </MenuItem>
                <MenuItem
                    autoFocus={false}
                    onClick={(event) => handleSelect(event, "FieldDock 0002")}
                    sx={{
                    color: "#000000",
                    backgroundColor: "#BEBEBE",
                    "&:hover": { backgroundColor: "#EBECF0" },
                    }}
                >
                    FieldDock 0002
                </MenuItem>
                <MenuItem
                    autoFocus={false}
                    onClick={(event) => handleSelect(event, "FieldDock 0003")}
                    sx={{
                    color: "#000000",
                    backgroundColor: "#BEBEBE",
                    "&:hover": { backgroundColor: "#EBECF0" },
                    }}
                >
                    FieldDock 0003
                </MenuItem>
                </Menu>
                {/* "Select Fielddock system....END" */}
            </div>
          <div style={{
            backgroundColor: '#ccc',
            height: '200px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            Box 2
          </div>
          <div style={{
            backgroundColor: '#ccc',
            height: '200px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            Box 3
          </div>
        </div>
      );
    }

export default Sensors;
