import { Container, Row, Col } from "react-bootstrap";
import React, { useState } from "react";
import CommonRow from "../../components/CommonRow";
import "../../CSS/imager_settings.css";

function Imager_Settings() {
  const [selectedInterval, setSelectedInterval] = useState(
    "Select time interval..."
  );

  const handleSelect = (event) => {
    setSelectedInterval(event.target.value);
  };

  return (
    <Container fluid>
      <div>
        <CommonRow />
      </div>
      {/* ------------------------------------------------- row 1 END ----------------------------- */}

      {[...Array(6)].map((_, i) => (
        <Col xs={6} sm={6} md={6} lg={6} xl={6} xxl={6} key={i}>
          <Row>
            <div>
              <div //div 1 styling
                style={{
                  position: "absolute",
                  top: "-15px",
                  left: "50%",
                  transform: "translateX(-50%)",
                  backgroundColor: "#181818",
                  padding: "0 5px",
                  zIndex: 1,
                  fontSize: "20px",
                }}
              >
                Sensor Modules
              </div>
              <p
                style={{
                  color: " #00e1b4",
                  marginLeft: "1rem",
                }}
              >
                Select time interval for: temperature, humidity, air pressure,
                lux, air quality (if integrated)
              </p>
              <div>
                <select
                  value={selectedInterval}
                  onChange={handleSelect}
                  style={{
                    backgroundColor: "transparent",
                    fontSize: "15px",
                    borderRadius: "5px",
                    border: "1px solid #797979",
                    color: "white",
                    padding: "10px", // Optional: add some padding for better appearance
                    marginRight: "2rem",
                  }}
                >
                  <option value="Select time interval..." disabled>
                    Select time interval...
                  </option>
                  <option value="5 minutes">5 minutes</option>
                  <option value="10 minutes">10 minutes</option>
                  <option value="15 minutes">15 minutes</option>
                  <option value="20 minutes">20 minutes</option>
                  <option value="45 minutes">45 minutes</option>
                  <option value="1 hour">1 hour</option>
                </select>
              </div>
            </div>
          </Row>
        </Col>
      ))}
    </Container>
  );
}

export default Imager_Settings;
