import { Box, Button, IconButton, Typography, useTheme, ListItemIcon } from "@mui/material";
import { tokens } from "../../theme";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { useState, useEffect } from "react";
import { ExpandMore } from "@mui/icons-material";
import axios from 'axios';



const Dashboard = () => {
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);


  // a state variable to keep track of the anchor element for the dropdown menu. This variable will be null initially and will be updated when the user clicks on the box
  const [anchorEl, setAnchorEl] = useState(null);
  const [anchorEl1, setAnchorEl1] = useState(null); //for misc sensors
  const [anchorEl2, setAnchorEl2] = useState(null); //for soil conditions sensors
  const [selectedSensor, setSelectedSensor] = useState(null);
  const [selectedSensor1, setSelectedSensor1] = useState(null);
  const [selectedSensor2, setSelectedSensor2] = useState(null);
  const [weatherData, setWeatherData] = useState(null);  //for weather data

  //two functions, one to handle the opening of the dropdown menu and another to handle the closing of the dropdown menu.
  const handleOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleSelect = (event, sensor) => {
    setSelectedSensor(sensor);
    handleClose();
  };

  //for misc sensors
  const handleOpen1 = (event) => {
    setAnchorEl1(event.currentTarget);
  };

  const handleSelect1 = (event, sensor) => {
    setSelectedSensor1(sensor);
    handleClose1();
  };

  const handleClose1 = () => {
    setAnchorEl1(null);
  };

  //for soil conditions sensors
  const handleOpen2 = (event) => {
    setAnchorEl2(event.currentTarget);
  };

  const handleClose2 = () => {
    setAnchorEl2(null);
  };

  const handleSelect2 = (event, sensor) => {
    setSelectedSensor2(sensor);
    handleClose2();
  };




//handles the text input for the "growing degree days" boxes
  const [startDate, setStartDate] = useState("");
//handles the text input for the "growing degree days" boxes
  const [endDate, setEndDate] = useState("");
//handles the text input for the "growing degree days" boxes
  const handleStartDateChange = (event) => {
    setStartDate(event.target.value);
  };
//handles the text input for the "growing degree days" boxes
  const handleEndDateChange = (event) => {
    setEndDate(event.target.value);
  };
//for live date and time
function getCurrentDateTimeInCST() {
  const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
  const date = new Date().toLocaleDateString('en-US', { timeZone: 'America/Chicago' });
  const dayOfWeek = daysOfWeek[new Date().getDay()];
  const time = new Date().toLocaleTimeString('en-US', { timeZone: 'America/Chicago' });
  return `${dayOfWeek} | ${date} | ${time} CST`;
}

//for the VisualCrossing Weather API
useEffect(() => {
  axios.get('https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/Saint%20Louis%20Missouri?unitGroup=us&key=6CWN6WAYFWA9MC4UXPLZG2TXU&contentType=json')
    .then(response => {
      setWeatherData(response.data);
    })
    .catch(error => {
      console.log(error);
    });
}, []);


  return (
    //parent style for all 3 main columns
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(3, 1fr)',
      gridGap: '10px',
      // margin: '0 auto',
      // maxWidth: '800px',
      height: '100vh',
       //the height of the parent container will be set to 100vh, which means it will take up the full height of the viewport. The width will adjust automatically based on the size of the screen. Note that this will cause the contents of each container to overflow horizontally if the screen is too narrow.
    }}
    >
      {/* style for column 1 */}
      <div style={{
        backgroundColor: 'transparent',
        height: '100%',
        display: 'flex',
        flexDirection: 'column', // this makes it so all the boxes in column 1 are stacked like a column on top of each other
        alignItems: 'center',
        justifyContent: 'center',
      }}>
        {/* box that surrounds fielddock logo */}
        <div style={{
          backgroundColor: 'transparent',
          height: '100px',
          display: 'flex',
          // marginTop: '14rem',
          alignItems: 'start',
          justifyContent: 'center',
          flexDirection: 'column', //this makes it so all the boxes in BOX 1 are stacked like a column  on top of eachother
        }}>
          {/* box that CONTAINS fielddock logo */}
          <div style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "500px",
            height: "100px",
            backgroundColor: "transparent",
            borderRadius: "10px",
          }}>
            <object 
              data="../../assets/FieldDock-Logo.svg" 
              type="image/svg+xml" 
              style={{ maxWidth: "100%", maxHeight: "100%" }}
              width="600px"
              height="200px"
            />
          </div>
        </div>
        <div>{/* "Select Fieldodck System....START" */}
          <Box
            sx={{
              border: "1px solid #00e1b4",
              padding: "10px",
              display: "flex",
              justifyContent: "space-between", // set the horizontal spacing between child elements
              alignItems: "center",
              width: "500px",
              marginTop: "1rem",
              height: "30px",
              backgroundColor: "transparent",
              borderRadius: "5px",
              fontSize: "17px",
              marginLeft: "10px"
            }}
            onClick={handleOpen}
          >
            {selectedSensor ? selectedSensor : "Select Fielddock System..."}
            <ListItemIcon>
              <ExpandMore />
            </ListItemIcon>
          </Box>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0001")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE !important",
                "&:hover": { backgroundColor: "#EBECF0 !important" }
              }}
            >
              FieldDock 0001
            </MenuItem>
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0002")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE",
                "&:hover": { backgroundColor: "#EBECF0" }
              }}
            >
              FieldDock 0002
            </MenuItem>
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0003")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE",
                "&:hover": { backgroundColor: "#EBECF0" }
              }}
            >
              FieldDock 0003
            </MenuItem>
          </Menu>
          {/* "Select Fielddock system....END" */}
        </div>
        <div>
          {/* Growing Degree Days....START possible API for this */}
          <Box //main outside box
            style={{
              border: "1px solid #797979", //originally #CCC
              padding: "10px",
              position: "relative",
              marginTop: "2rem",
              width: "500px",
              height: "100px",
              borderRadius: "10px",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <Box
              style={{
                position: "absolute",
                top: "-15px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "#181818",
                padding: "0 5px",
                zIndex: 1,
                fontSize: "20px",
              }}
            >
              Growing Degree Days
            </Box>
            <Box //start date box
              style={{
                border: "1px solid #CCC",
                width: "25%",
                height: "50px",
                display: "inline-block",
                borderRadius: "5px",
                fontSize: "20px",
                marginRight: "10px",
              }}
            >
              <input
                type="text"
                style={{
                  width: "100%",
                  height: "100%",
                  border: "none",
                  background: "none",
                  color: "#999",
                  fontSize: "20px",
                  textAlign: "center",
                  outline: "none",
                  fontSize: "15px",
                }}
                placeholder="Start Date"
                value={startDate}
                onChange={handleStartDateChange}
              />
            </Box>
            <Box //end date box
              style={{
                border: "1px solid #CCC",
                width: "25%",
                height: "50px",
                display: "inline-block",
                borderRadius: "5px",
                fontSize: "20px",
                marginRight: "10px",
              }}
            >
              <input
                type="text"
                style={{
                  width: "100%",
                  height: "100%",
                  border: "none",
                  background: "none",
                  color: "#999",
                  fontSize: "15px",
                  textAlign: "center",
                  outline: "none",
                }}
                placeholder="End Date"
                value={endDate}
                onChange={handleEndDateChange}
              />
            </Box>
            <Box //box for Enter button
              style={{
                border: "1px solid #f59331",
                width: "25%",
                height: "50px",
                display: "inline-block",
                borderRadius: "5px",
              }}
            >
              <button
                style={{
                  width: "100%",
                  height: "100%",
                  border: "none",
                  background: "none",
                  color: "#ffffff",
                  borderRadius: "10px",
                  fontSize: "20px",
                  cursor: "pointer",
                }}
              >
                Enter
              </button>
            </Box>
            <Box //box for GDD and 000 measurement
              style={{
                width: "25%",
                height: "40px",
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: "5px",
              }}
            >
              <Box
                style={{
                  color: "#FFF",
                  fontSize: "15px",
                }}
              >
                GDD:
              </Box>
              <Box
                style={{
                  color: "#999",
                  fontSize: "15px",
                }}
              >
                000
              </Box>
            </Box>
          </Box>
        </div>
        <div>  {/*misc box starts here*/}
          <Box  
            style={{
              border: "1px solid #797979",
              padding: "10px",
              position: "relative",
              marginTop: "2rem",
              width: "500px",
              height: "300px",
              // marginLeft: "200px",
              borderRadius: "10px"
            }}
          >
            <Box
              style={{
                position: "absolute",
                top: "-15px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "#181818",
                padding: "0 5px",
                zIndex: 1,
                fontSize: "20px"
              }}
            >
              Misc
            </Box>
            <div>{/* "Select misc sensor....START" */}
              <Box
                sx={{
                  border: "1px solid #00e1b4",
                  borderRadius: "4px",
                  padding: "10px",
                  display: "flex",
                  justifyContent: "space-between", // set the horizontal spacing between child elements
                  alignItems: "center",
                  width: "460px",
                  marginTop: "1rem",
                  height: "30px",
                  backgroundColor: "transparent",
                  borderRadius: "5px",
                  fontSize: "17px",
                  marginLeft: "10px"
                }}
                onClick={handleOpen1}
              >
                {selectedSensor1 ? selectedSensor1 : "Select a sensor..."}
                <ListItemIcon>
                  <ExpandMore />
                </ListItemIcon>
              </Box>
              <Menu anchorEl={anchorEl1} open={Boolean(anchorEl1)} onClose={handleClose1}>
                <MenuItem
                  onClick={(event) => handleSelect1(event, "Apogee SQ521 PAR Light")}
                  sx={{
                    color: "#000000",
                    backgroundColor: "#BEBEBE !important",
                    "&:hover": { backgroundColor: "#EBECF0 !important" }
                  }}
                >
                  Apogee SQ521 PAR Light
