import React, { useState, useEffect } from "react";
import { Box } from "@mui/material";
// import mqtt from "mqtt";

const Status = () => {
  const [messages, setMessages] = useState([]); // Store MQTT messages in state

  useEffect(() => {
    // Create an MQTT client and connect to your broker
    const client = mqtt.connect("ws://3.145.131.67:9001");

    // Subscribe to the "drone/status" topic
    client.subscribe("drone/status");

    // Handle incoming MQTT messages
    client.on("message", (topic, message) => {
      // Update the messages state with the new message
      setMessages((prevMessages) => [...prevMessages, message.toString()]);
    });

    // Clean up the MQTT connection when the component unmounts
    return () => {
      client.end();
    };
  }, []);

  return (
    <>
      <Box
        style={{
          borderTop: "1px solid #f59331",
          width: "80%",
          height: "500px",
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-start",
          marginBottom: "15px",
        }}
      >
        <Box
          style={{
            position: "absolute",
            transform: "translate(-1%, -60%)",
            backgroundColor: "#181818",
            padding: "0 7px",
            zIndex: 1,
            fontSize: "25px",
          }}
        >
          System Controllerss
        </Box>
        <div
          style={{
            marginTop: "40px",
            color: "white",
          }}
        >
          {messages.map((message, index) => (
            <p key={index}>{message}</p>
          ))}
        </div>
      </Box>
    </>
  );
};

export default Status;
