import React from "react";
import { Box, ListItemIcon, Typography } from "@mui/material";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { useState, useEffect } from "react";
import { ExpandMore } from "@mui/icons-material";
import { Link } from "react-router-dom";
import Chart from "../components/Chart";
import axios from "axios";

const Plug_Play = () => {
  //--------------------------------------weather data-----------------------------------------------
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    const interval = setInterval(() => {
      axios
        .get("http://3.145.131.67:8000/weather/data/")
        .then((response) => {
          setWeatherData(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }, 60000);
    return () => clearInterval(interval);
  }, []);
  //-------------------------------------weather data end--------------------------------------
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedSensor, setSelectedSensor] = useState(null);
  const [selectedSensor2, setSelectedSensor2] = useState(null); //plug and play sensor
  const [anchorEl2, setAnchorEl2] = useState(null); //for plug and play sensors
  const [showChart, setShowChart] = useState(false);
  const [sensors, setSensors] = useState([]); //wireless sensor network "connected sensors" portion

  const [hangerAnchorEl, setHangerAnchorEl] = useState(null);
  const [selectedHangerSystem, setSelectedHangerSystem] = useState(null);
  const [hangerSystems, setHangerSystems] = useState([]);

  const [data, setData] = useState([]); //used for when you select a sensor in the Plug_Play box, that the sensor you picked, shows the data

  const handleOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleSelect = (event, sensor) => {
    setSelectedSensor(sensor);
    handleClose();
  };

  //for plug and play sensors
  const handleOpen2 = (event) => {
    setAnchorEl2(event.currentTarget);
  };

  const handleClose2 = () => {
    setAnchorEl2(null);
  };

  const handleSelect2 = (event, device) => {
    setSelectedSensor2(`${device}`);
    handleClose2(event);
  };

  //handles the text input for the "growing degree days" boxes
  const [startDate, setStartDate] = useState("");
  //handles the text input for the "growing degree days" boxes
  const [endDate, setEndDate] = useState("");
  //handles the text input for the "growing degree days" boxes
  const handleStartDateChange = (event) => {
    setStartDate(event.target.value);
  };
  //handles the text input for the "growing degree days" boxes
  const handleEndDateChange = (event) => {
    setEndDate(event.target.value);
  };

  //for live date and time
  function getCurrentDateTimeInCST() {
    const daysOfWeek = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    const date = new Date().toLocaleDateString("en-US", {
      timeZone: "America/Chicago",
    });
    const dayOfWeek = daysOfWeek[new Date().getDay()];
    const time = new Date().toLocaleTimeString("en-US", {
      timeZone: "America/Chicago",
    });
    return `${dayOfWeek} | ${date} | ${time} CST`;
  }

  useEffect(() => {
    const intervalID = setInterval(() => {
      ////wireless sensor network "connected sensors" portion
      fetch("http://3.145.131.67:8000/syssen/sensors/")
        .then((response) => response.json())
        .then((data) => {
          if (data.length > 0) {
            setSensors(data);

            // Count the number of unique devices
            const devices = new Set();
            data.forEach((sensor) => {
              devices.add(sensor.device);
            });
            setNumDevices(devices.size);
          } else {
            sensorName("No sensors detected....");
            setNumDevices(0);
          }
        })
        .catch((error) => console.log(error));
    }, 5000);

    return () => clearInterval(intervalID);
  }, []);
  const sensorName = sensors.length > 0 ? sensors[0].name : "Loading...";

  // Initialize a set to keep track of unique devices
  const devices = new Set();

  // Loop through each sensor in the sensors array
  sensors.forEach((sensor) => {
    // Add the device ID to the set
    devices.add(sensor.device);
  });

  // Define a state variable for the number of devices
  const [numDevices, setNumDevices] = useState(0);

  useEffect(() => {
    //logic for selecting which Fieldock system you want, would be the selection of the main system
    fetch("http://3.145.131.67:8000/hanger/hanger-system/")
      .then((response) => response.json())
      .then((data) => setHangerSystems(data))
      .catch((error) => console.log(error));
  }, []);

  const handleHangerOpen = (event) => {
    setHangerAnchorEl(event.currentTarget);
  };

  const handleHangerClose = () => {
    setHangerAnchorEl(null);
  };

  const handleHangerSelect = (event, selected) => {
    setSelectedHangerSystem(selected);
    handleHangerClose();
  };

  return (
    <div //parent div for ALL COLUMNS
      style={{
        display: "flex",
        justifyContent: "space-between",
        height: "100vh",
        flexWrap: "wrap", // Allow the boxes to wrap if the screen size is too small
      }}
    >
      {/* STYLE FOR COLUMN ONE */}
      <div
        style={{
          backgroundColor: "transparent",
          height: "100%",
          display: "flex",
          flexDirection: "column", // this makes it so all the boxes in column 1 are stacked like a column on top of each other
          alignItems: "center",
          justifyContent: "center",
          flex: "1", // Give equal width to each box
          minWidth: "calc(33.333% - 10px)", // Calculate the width for each box, considering the space between them
          margin: "0 5px", // Set horizontal margin to 5px for equal spacing
          marginBottom: "10px", // Set vertical margin between wrapped boxes
        }}
      >
        {/* box that surrounds fielddock logo */}
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "85%", // Set width to 100% of the parent
            height: "100px",
            backgroundColor: "transparent",
          }}
        >
          <object
            data="../../assets/FieldDock-Logo.svg"
            type="image/svg+xml"
            style={{ maxWidth: "100%", maxHeight: "100%" }} // Apply maxWidth and maxHeight directly to the object
            // Remove fixed width and height values
          />
        </div>
        <div style={{ width: "85%" }}>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              height: "40px",
              backgroundColor: "transparent",
              border: "1px solid #00e1b4",
              padding: "10px",
              justifyContent: "space-between",
              marginTop: "3rem",
              borderRadius: "5px",
              fontSize: "1vw", // dynamic font size based on viewport width
            }}
            onClick={handleHangerOpen}
          >
            {selectedHangerSystem
              ? selectedHangerSystem
              : "Select Hanger System..."}
            <ListItemIcon>
              <ExpandMore />
            </ListItemIcon>
          </div>

          <Menu
            anchorEl={hangerAnchorEl}
            open={Boolean(hangerAnchorEl)}
            onClose={handleHangerClose}
          >
            {hangerSystems.map((system) => (
              <MenuItem
                key={system.id}
                autoFocus={false}
                onClick={(event) =>
                  handleHangerSelect(
                    event,
                    `${system.name} - ${system.location}`
                  )
                }
                sx={{
                  color: "#000000",
                  backgroundColor: "#BEBEBE !important",
                  "&:hover": { backgroundColor: "#EBECF0 !important" },
                }}
              >
                {system.name} - {system.location}
              </MenuItem>
            ))}
          </Menu>
        </div>{" "}
        {/* end of SELECT FIELDDOCK */}
        <div style={{ width: "85%" }}>
          {" "}
          {/* beginning of WIRELESS SENSOR */}
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              height: "40px",
              backgroundColor: "transparent",
              border: "1px solid #00e1b4",
              padding: "10px",
              justifyContent: "space-between",
              marginTop: "3rem",
              borderRadius: "5px",
              fontSize: "1vw", // dynamic font size based on viewport width
            }}
            onClick={handleOpen}
          >
            {selectedSensor ? selectedSensor : "Select Wireless Sensor..."}
            <ListItemIcon>
              <ExpandMore />
            </ListItemIcon>
          </div>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0001")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE !important",
                "&:hover": { backgroundColor: "#EBECF0 !important" },
              }}
            >
              FieldDock 0001
            </MenuItem>
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0002")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE",
                "&:hover": { backgroundColor: "#EBECF0" },
              }}
            >
              FieldDock 0002
            </MenuItem>
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0003")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE",
                "&:hover": { backgroundColor: "#EBECF0" },
              }}
            >
              FieldDock 0003
            </MenuItem>
          </Menu>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {/* Plug and Play Sensor START */}
          <Box
            // onClick={() => setShowChart(true)}
            // onBlur={() => setShowChart(false)}
            style={{
              border: "1px solid #797979",
              padding: "10px",
              position: "relative",
              marginTop: "4rem",
              width: "85%", // Changed to 75% of column
              height: "490px",
              borderRadius: "5px",
            }}
          >
            <Box
              style={{
                position: "absolute",
                top: "-15px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "#181818",
                padding: "0 5px",
                zIndex: 1,
                fontSize: "1vw", // dynamic font size based on viewport width
              }}
            >
              Plug and Play Sensor
              {showChart && (
                <div
                  style={{
                    position: "fixed",
                    top: "50%",
                    left: "50%",
                    transform: "translate(200%, -50%)",
                    zIndex: 999,
                    width: "100%",
                    height: "100%",
                  }}
                >
                  <Chart />
                </div>
              )}
            </Box>
            <div>
              {/* "Select sensor....START" */}
              <Box
                sx={{
                  //styling for the "Select a sensor box"
                  border: "1px solid #00e1b4",
                  borderRadius: "4px",
                  padding: "10px",
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  maxWidth: "100%", // Changed to 100% to shrink proportionally
                  maxHeight: "100%", // Changed to 100% to shrink proportionally
                  marginTop: "1rem",
                  height: "30px",
                  backgroundColor: "transparent",
                  borderRadius: "5px",
                  fontSize: "1vw", // dynamic font size based on viewport width
                  marginLeft: "10px",
                }}
                onClick={handleOpen2}
              >
                {selectedSensor2 ? selectedSensor2 : "Select a sensor..."}
                <ListItemIcon>
                  <ExpandMore />
                </ListItemIcon>
              </Box>
              <Menu
                anchorEl={anchorEl2}
                open={Boolean(anchorEl2)}
                onClose={handleClose2}
              >
                {sensors.length > 0 ? (
                  // Get the unique devices from the sensors array
                  [...new Set(sensors.map((sensor) => sensor.device))].map(
                    (device) => (
                      <MenuItem
                        key={device}
                        onClick={(event) =>
                          handleSelect2(event, `Device ${device}`)
                        }
                        sx={{
                          color: "#000000",
                          backgroundColor: "#BEBEBE !important",
                          "&:hover": { backgroundColor: "#EBECF0 !important" },
                        }}
                      >
                        Device {device}
                      </MenuItem>
                    )
                  )
                ) : (
                  <MenuItem
                    disabled
                    sx={{
                      color: "#000000",
                      backgroundColor: "#BEBEBE !important",
                      "&:hover": { backgroundColor: "#EBECF0 !important" },
                    }}
                  >
                    No sensors available
                  </MenuItem>
                )}
              </Menu>
              {/* "Select soil sensor....END" */}
              {/* Table START */}
              <Box
                sx={{
                  marginTop: "1rem",
                  width: "100%",
                  overflow: "auto",
                }}
              >
                <div
                  style={{
                    height: "400px",
                    overflow: "auto",
                    fontSize: "0.8vw",
                  }}
                >
                  <table style={{ width: "100%", tableLayout: "fixed" }}>
                    <thead>
                      <tr>
                        <th
                          style={{
                            color: "#e8e8e8",
                            width: "25%",
                            padding: "10px",
                          }}
                        >
                          ID:
                        </th>
                        <th
                          style={{
                            color: "#e8e8e8",
                            width: "25%",
                            padding: "10px",
                          }}
                        >
                          Type:
                        </th>
                        <th
                          style={{
                            color: "#e8e8e8",
                            width: "25%",
                            padding: "10px",
                          }}
                        >
                          Value:
                        </th>
                        <th
                          style={{
                            color: "#e8e8e8",
                            width: "25%",
                            padding: "10px",
                          }}
                        >
                          Device:
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      {data
                        .filter(
                          (datum) => datum.device === selectedSensor2.device
                        ) // Filter the data by device ID
                        .map((datum) => (
                          <tr key={datum.id}>
                            <td
                              style={{
                                borderBottom: "1px solid #d3d3d3",
                                padding: "10px",
                                textAlign: "center",
                                borderBottomWidth: "1px",
                                width: "25%",
                                color: "#00e1b4",
                              }}
                            >
                              {datum.id}
                            </td>
                            <td
                              style={{
                                borderBottom: "1px solid #d3d3d3",
                                padding: "10px",
                                textAlign: "center",
                                borderBottomWidth: "1px",
                                width: "25%",
