import React from "react";
import { Box, ListItemIcon } from "@mui/material";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { useState, useEffect } from "react";
import { ExpandMore } from "@mui/icons-material";
import { Link } from "react-router-dom";
import Chart from "../components/Chart";
// import axios from 'axios';

const Plug_Play = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedSensor, setSelectedSensor] = useState(null);
  const [selectedSensor2, setSelectedSensor2] = useState(null); //plug and play sensor
  const [anchorEl2, setAnchorEl2] = useState(null); //for plug and play sensors
  const [showChart, setShowChart] = useState(false);
  const [sensors, setSensors] = useState([]); //wireless sensor network "connected sensors" portion

  const handleOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleSelect = (event, sensor) => {
    setSelectedSensor(sensor);
    handleClose();
  };

  //for plug and play sensors
  const handleOpen2 = (event) => {
    setAnchorEl2(event.currentTarget);
  };

  const handleClose2 = () => {
    setAnchorEl2(null);
  };

  const handleSelect2 = (event, sensor) => {
    setSelectedSensor2(sensor);
    handleClose2();
  };

  //handles the text input for the "growing degree days" boxes
  const [startDate, setStartDate] = useState("");
  //handles the text input for the "growing degree days" boxes
  const [endDate, setEndDate] = useState("");
  //handles the text input for the "growing degree days" boxes
  const handleStartDateChange = (event) => {
    setStartDate(event.target.value);
  };
  //handles the text input for the "growing degree days" boxes
  const handleEndDateChange = (event) => {
    setEndDate(event.target.value);
  };

  //for live date and time
  function getCurrentDateTimeInCST() {
    const daysOfWeek = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    const date = new Date().toLocaleDateString("en-US", {
      timeZone: "America/Chicago",
    });
    const dayOfWeek = daysOfWeek[new Date().getDay()];
    const time = new Date().toLocaleTimeString("en-US", {
      timeZone: "America/Chicago",
    });
    return `${dayOfWeek} | ${date} | ${time} CST`;
  }

  useEffect(() => {
    const fetchData = () => {
      fetch("http://3.145.131.67:8000/syssen/sensors/")
        .then((response) => response.json())
        .then((data) => {
          if (data.length > 0) {
            setSensors(data);
            sensorName("All Operational");
          } else {
            sensorName("None Detected");
          }
        })
        .catch((error) => console.log(error));
    };

    fetchData(); // initial fetch when the component mounts

    const intervalId = setInterval(fetchData, 5000); // fetch data every 5 seconds

    return () => clearInterval(intervalId); // cleanup function to clear the interval when the component unmounts
  }, []);

  const sensorName = sensors.length > 0 ? sensors[0].name : "Loading...";

  return (
    <div //parent div for ALL COLUMNS
      style={{
        display: "flex",
        justifyContent: "space-between",
        height: "100vh",
        flexWrap: "wrap", // Allow the boxes to wrap if the screen size is too small
      }}
    >
      {/* STYLE FOR COLUMN ONE */}
      <div
        style={{
          backgroundColor: "transparent",
          height: "100%",
          display: "flex",
          flexDirection: "column", // this makes it so all the boxes in column 1 are stacked like a column on top of each other
          alignItems: "center",
          justifyContent: "center",
          flex: "1", // Give equal width to each box
          minWidth: "calc(33.333% - 10px)", // Calculate the width for each box, considering the space between them
          margin: "0 5px", // Set horizontal margin to 5px for equal spacing
          marginBottom: "10px", // Set vertical margin between wrapped boxes
        }}
      >
        {/* box that surrounds fielddock logo */}
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "85%", // Set width to 100% of the parent
            height: "100px",
            backgroundColor: "transparent",
          }}
        >
          <object
            data="../../assets/FieldDock-Logo.svg"
            type="image/svg+xml"
            style={{ maxWidth: "100%", maxHeight: "100%" }} // Apply maxWidth and maxHeight directly to the object
            // Remove fixed width and height values
          />
        </div>
        <div style={{ width: "85%" }}>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              height: "40px",
              backgroundColor: "transparent",
              border: "1px solid #00e1b4",
              padding: "10px",
              justifyContent: "space-between",
              marginTop: "3rem",
              borderRadius: "5px",
              fontSize: "1vw", // dynamic font size based on viewport width
            }}
            onClick={handleOpen}
          >
            {selectedSensor ? selectedSensor : "Select Fielddock System..."}
            <ListItemIcon>
              <ExpandMore />
            </ListItemIcon>
          </div>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0001")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE !important",
                "&:hover": { backgroundColor: "#EBECF0 !important" },
              }}
            >
              FieldDock 0001
            </MenuItem>
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0002")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE",
                "&:hover": { backgroundColor: "#EBECF0" },
              }}
            >
              FieldDock 0002
            </MenuItem>
            <MenuItem
              autoFocus={false}
              onClick={(event) => handleSelect(event, "FieldDock 0003")}
              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE",
                "&:hover": { backgroundColor: "#EBECF0" },
              }}
            >
              FieldDock 0003
            </MenuItem>
          </Menu>
        </div>{" "}
        {/* end of SELECT FIELDDOCK */}
        <div style={{ width: "85%" }}>
          {" "}
          {/* beginning of WIRELESS SENSOR */}
          <div
            style={{
