              sx={{
                color: "#000000",
                backgroundColor: "#BEBEBE",
                "&:hover": { backgroundColor: "#EBECF0" },
              }}
            >
              FieldDock 0003
            </MenuItem>
          </Menu>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {/* Plug and Play Sensor START */}
          <Box
            onClick={() => setShowChart(true)}
            onBlur={() => setShowChart(false)}
            style={{
              border: "1px solid #797979",
              padding: "10px",
              position: "relative",
              marginTop: "4rem",
              width: "85%", // Changed to 75% of column
              height: "490px",
              borderRadius: "5px",
            }}
          >
            <Box
              style={{
                position: "absolute",
                top: "-15px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "#181818",
                padding: "0 5px",
                zIndex: 1,
                fontSize: "1vw", // dynamic font size based on viewport width
              }}
            >
              Plug and Play Sensor
              {showChart && (
                <div
                  style={{
                    position: "fixed",
                    top: "50%",
                    left: "50%",
                    transform: "translate(200%, -50%)",
                    zIndex: 999,
                    width: "100%",
                    height: "100%",
                  }}
                >
                  <Chart />
                </div>
              )}
            </Box>
            <div>
              {/* "Select sensor....START" */}
              <Box
                sx={{
                  //styling for the "Select a sensor box"
                  border: "1px solid #00e1b4",
                  borderRadius: "4px",
                  padding: "10px",
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  maxWidth: "100%", // Changed to 100% to shrink proportionally
                  maxHeight: "100%", // Changed to 100% to shrink proportionally
                  marginTop: "1rem",
                  height: "30px",
                  backgroundColor: "transparent",
                  borderRadius: "5px",
                  fontSize: "1vw", // dynamic font size based on viewport width
